mapper.update:
   
      UPDATE   BOARD
        SET    STEP     =  STEP + 1
        WHERE  MENU_ID  =  #{ menu_id }
	    AND    NREF     =  #{ nref    }
	    AND    STEP     >  #{ step }

insert.foreach:
     INTO   FILES   VALUES (
        GET_FILENUM() 
		, ( SELECT  MAX(IDX)   FROM BOARD  )
		, #{ file.filename   }     
		, #{ file.fileext    }     
		, #{ file.sfilename  }     
     )
